// Transform ERPNext product to Webflow format
const product = $input.item.json;
const isUpdate = product.webflow_item_id ? true : false;

// Build attribute links for Webflow multi-reference fields
// These link products to their applicable attribute collection items
const attributeLinks = product.attribute_links || [];
const attributeLinksByType = product.attribute_links_by_type || {};

// Build multi-reference arrays for each attribute type
// Each array contains Webflow item IDs that can be used in multi-reference fields
const finishRefs = (attributeLinksByType['Finish'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const lensRefs = (attributeLinksByType['Lens Appearance'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const mountingRefs = (attributeLinksByType['Mounting Method'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const cctRefs = (attributeLinksByType['CCT'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const outputLevelRefs = (attributeLinksByType['Output Level'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const envRatingRefs = (attributeLinksByType['Environment Rating'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const endcapStyleRefs = (attributeLinksByType['Endcap Style'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const powerFeedRefs = (attributeLinksByType['Power Feed Type'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
const ledPackageRefs = (attributeLinksByType['LED Package'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);
// DISABLED: CRI multi-reference field not available (out of multi-ref fields)
// const criRefs = (attributeLinksByType['CRI'] || []).filter(a => a.webflow_item_id).map(a => a.webflow_item_id);

// Build specifications as structured data for Webflow collection filtering
// Legacy: kept for backwards compatibility
const specifications = [];
if (product.specifications && product.specifications.length > 0) {
  for (const spec of product.specifications) {
    const specData = {
      spec_group: spec.spec_group,
      spec_label: spec.spec_label,
      spec_value: spec.spec_value,
      spec_unit: spec.spec_unit || '',
      display_order: spec.display_order,
      show_on_card: spec.show_on_card,
      attribute_doctype: spec.attribute_doctype || null,
      attribute_options: spec.attribute_options || []
    };
    specifications.push(specData);
  }
}

// Build specifications HTML for legacy rich text field (backwards compatible)
let specsHtml = '';
if (product.specifications && product.specifications.length > 0) {
  specsHtml = '<ul>';
  for (const spec of product.specifications) {
    specsHtml += `<li><strong>${spec.spec_label}:</strong> ${spec.spec_value}${spec.spec_unit ? ' ' + spec.spec_unit : ''}</li>`;
  }
  specsHtml += '</ul>';
}

// Build base field data (without slug for updates)
const fieldData = {
  "name": product.product_name,
  "product-type": product.product_type,
  "short-description": product.short_description || '',
  "long-description": product.long_description || '',
  "is-configurable-3": product.is_configurable === 1 || product.is_configurable === true,
  "min-length-mm": product.min_length_mm || 0,
  "max-length-mm": product.max_length_mm || 0,
  "erp-sync-id": product.product_slug,
  "specifications-html": specsHtml,
  "specifications-json": JSON.stringify(specifications),
  // New: Attribute links as JSON for reference
  "attribute-links-json": JSON.stringify(attributeLinks)
};

// Add series reference field if product has a series with Webflow item ID
// Series is a single-reference field (not multi-reference)
if (product.series && product.series_webflow_item_id) {
  fieldData["series"] = product.series_webflow_item_id;
}

// Add multi-reference fields if they have values
// These map to Webflow collection multi-reference fields
if (finishRefs.length > 0) fieldData["finishes"] = finishRefs;
if (lensRefs.length > 0) fieldData["lens-options"] = lensRefs;
if (mountingRefs.length > 0) fieldData["mounting-methods"] = mountingRefs;
if (cctRefs.length > 0) fieldData["cct-options"] = cctRefs;
if (outputLevelRefs.length > 0) fieldData["output-levels"] = outputLevelRefs;
if (envRatingRefs.length > 0) fieldData["environment-ratings"] = envRatingRefs;
if (endcapStyleRefs.length > 0) fieldData["endcap-styles"] = endcapStyleRefs;
if (powerFeedRefs.length > 0) fieldData["power-feed-types"] = powerFeedRefs;
if (ledPackageRefs.length > 0) fieldData["led-packages"] = ledPackageRefs;
// DISABLED: CRI multi-reference field not available
// if (criRefs.length > 0) fieldData["cri-options"] = criRefs;

// Only include slug on create (not update)
if (!isUpdate) {
  fieldData["slug"] = product.product_slug;
}

// Build Webflow payload
const webflowData = {
  isArchived: false,
  isDraft: false,
  fieldData: fieldData
};

// Add featured image if available
if (product.featured_image) {
  webflowData.fieldData["featured-image"] = product.featured_image;
}

return {
  json: {
    webflowData,
    originalProduct: product,
    specifications: specifications,
    attributeLinks: attributeLinks,
    attributeLinksByType: attributeLinksByType
  }
};
